				<label>Path :&nbsp;</label><input type="text" size="65" maxlength="100" id="path" name="path" value="<?php echo $site_path ?>" class="text" />
			</dd>	
		</dl>
		<div>
			<input type="submit" name="submit" value="Submit" class="submit" />
		</div>
	</fieldset>
</form>
